<?PHPclass Core_Dispatcher{    public static $_CONTROLLER = null;    public static $_ACTION = null;    public static $_PARAMETERS = array ();    public static $_NAVIGATE = null;    public static $_ARTICLE = null;    public function __construct ()    {        $parameters = explode ('?',$_SERVER['REQUEST_URI']);        $tmp = explode ('/', $parameters [0].'/');        $count = 0; $parameters = array (); $key = '';        foreach ( $tmp as $str )        {            if ( $str == "" ) continue;            elseif ($count == 0) { self::$_CONTROLLER = $str;  }            elseif ($count == 1) { self::$_ACTION = $str;  }            elseif (($count % 2) == 0) { $key = $str; }            else {                self::$_PARAMETERS [$key] = $str;                $key = '';            }            $count ++;        }        if ($key != '') self::$_PARAMETERS[$key] = null;        if (self::$_CONTROLLER == null) {            self::$_CONTROLLER = 'index';            self::$_ACTION = 'index';        } elseif (self::$_ACTION == null) {            self::$_ACTION = self::$_CONTROLLER;            self::$_CONTROLLER = 'index';        }        $tmp = $_REQUEST;        if (!empty ($tmp)) {            foreach ($tmp as $key => $var) self::$_PARAMETERS [$key] = $var;        }        core::$_DISPATCHER = (object) array ('_CONTROLLER' => null, '_ACTION' => null, '_PARAMETERS' => null, '_NAVIGATE' => null, '_ARTICLE' => null);        core::$_DISPATCHER->_CONTROLLER = self::$_CONTROLLER;        core::$_DISPATCHER->_ACTION = self::$_ACTION;        core::$_DISPATCHER->_PARAMETERS = self::$_PARAMETERS;        core::$_DISPATCHER->_NAVIGATE = self::$_NAVIGATE;        core::$_DISPATCHER->_ARTICLE = self::$_ARTICLE;    }    function __destruct()    {        core::$_DISPATCHER->_CONTROLLER = self::$_CONTROLLER;        core::$_DISPATCHER->_ACTION = self::$_ACTION;        core::$_DISPATCHER->_PARAMETERS = self::$_PARAMETERS;        core::$_DISPATCHER->_NAVIGATE = self::$_NAVIGATE;        core::$_DISPATCHER->_ARTICLE = self::$_ARTICLE;    }}