<?PHPclass Core_Model{    protected $_db = null;    protected $_VARS = null;    protected $_ROUTE = null;    function __construct( $params = null)    {        if (class_exists ('Db_MySqli')) { $this->_db = clone Db_MySqli::$_BASE_CONNECTION; }        $this->_ROUTE = &Core::$_DISPATCHER->_NAVIGATE;        $this->_VARS = &Core::$_DISPATCHER->_PARAMETERS;        if (method_exists ( $this, 'init')) $this->init ($params);    }}